import React, { useState } from "react";


export default function ButtonHandler({type,className, dataTestid, className2,timerObject, setTimerObject} )
{
    const incrementHandler = (event) => {
        
        console.log("increment",type)
    }

    const decrementHandler = (event) => {
        console.log("decrement", event)
    }
    if(dataTestid.includes("increase"))
    {
        return(
            <button
            type={type}
            className={className}
            data-testid={dataTestid}
            onClick={incrementHandler}
          >
           <span className={className2} />
          </button>
        )
    }
   else {
    return(
        <button
        type={type}
        className={className}
        data-testid={dataTestid}
        onClick={decrementHandler}
      >
       <span className={className2} />
      </button>
    )
   }
}
