import React, { useState } from "react";
import { minutesToDuration } from "../utils/duration";
import { secondsToDuration } from "../utils/duration";
import TimerSectionSpecific from "./TimerSectionSpecific";
export default function TimerSection ({timerObject, setTimerObject,isTimerRunning})
{
    switch(timerObject.state)
    {
        case "stop":
            return (
                <div></div>
            )
            break;
        case "focus":
            if(isTimerRunning)
            {
            let width = 1-((timerObject.focusSeconds)/(timerObject.focusDuration*60));
            width = width*100;
            console.log(width)
            return <TimerSectionSpecific durationString={ `Focusing for ${minutesToDuration(timerObject.focusDuration)} minutes`} width ={width} timeString={`${secondsToDuration(timerObject.focusSeconds)} remaining`}/>
            // return (
            //     <div>
            //         {/* TODO: This area should show only when a focus or break session is running or pauses */}
            //         <div className="row mb-2">
            //             <div className="col">
            //             {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            //             <h2 data-testid="session-title">
            //              Focusing for {minutesToDuration(timerObject.focusDuration)} minutes
            //             </h2>
            //             {/* TODO: Update message below to include time remaining in the current session */}
            //          <p className="lead" data-testid="session-sub-title">
            //           {secondsToDuration(timerObject.focusSeconds)} remaining
            //         </p>
            //       </div>
            //     </div>
            //     <div className="row mb-2">
            //       <div className="col">
            //         <div className="progress" style={{ height: "20px" }}>
            //           <div
            //             className="progress-bar"
            //             role="progressbar"
            //             aria-valuemin="0"
            //             aria-valuemax="100"
            //             aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
            //             style={{ width: `${width}%` }} // TODO: Increase width % as elapsed time increases
            //           />
            //         </div>
            //       </div>
            //     </div>
            //   </div>
            // )
            }
            break;
            case "break":
                if(isTimerRunning)
                {
                let width = 1-((timerObject.breakSeconds)/(timerObject.breakDuration*60));
                width = width*100;
                console.log(width)
                return (
                    <div>
                        {/* TODO: This area should show only when a focus or break session is running or pauses */}
                        <div className="row mb-2">
                            <div className="col">
                            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
                            <h2 data-testid="session-title">
                            Break for {minutesToDuration(timerObject.breakDuration)} minutes
                            </h2>
                            {/* TODO: Update message below to include time remaining in the current session */}
                         <p className="lead" data-testid="session-sub-title">
                          {secondsToDuration(timerObject.breakSeconds)} remaining
                        </p>
                      </div>
                    </div>
                    <div className="row mb-2">
                      <div className="col">
                        <div className="progress" style={{ height: "20px" }}>
                          <div
                            className="progress-bar"
                            role="progressbar"
                            aria-valuemin="0"
                            aria-valuemax="100"
                            aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                            style={{ width: `${width}%` }} // TODO: Increase width % as elapsed time increases
                          />
                        </div>
                      </div>
                    </div>
                  </div>
                )
                }  
        }
    
   
}
